package Mahesh;

public class ifElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x=10;
if (x%2==0) {
	System.out.println("x value is Even: " +x);
}
else {
	System.out.println("X value is Odd: "+x);
}

int y=11;
if (y%2==0) {
	System.out.println("y value is Even: " +y);
}
else {
	System.out.println("y value is Odd: "+y);
}

}

}
